let end = new Date();
let start = new Date();
start.setDate(end.getDate() - 1); // last 7 days.

msg.payload = {
    data : {
        from : start,
        to : end
    }
}
return msg;

if (msg.payload.data){
    let measurement = msg.payload.data.measurement;

    let temperatureData = [];
    let pressureData = [];
    let flowrateData = [];

    for (const item of measurement) {
        let date = new Date(item.timestamp);
        var timestamp = date; //Math.floor(date.getTime() / 1000); // unix

        let temperaturePoint = {
            x : timestamp,
            y: item.temperature_guard
        };
        temperatureData.push(temperaturePoint);

        let pressurePoint = {
            x: timestamp,
            y: item.pressure
        };
        pressureData.push(pressurePoint);

        let flowratePoint = {
            x: timestamp,
            y: item.flowrate
        };
        flowrateData.push(flowratePoint);
    }

    let msg1 = { 
        payload : [{
            series: ["Temperature"],
            data: [temperatureData],
            labels: ["SenseGuard"]
        }]
    };

    let msg2 = {
        payload: [{
            series: ["Pressure"],
            data: [pressureData],
            labels: ["SenseGuard"]
        }]
    };

    let msg3 = {
        payload: [{
            series: ["Flowrate"],
            data: [flowrateData],
            labels: ["SenseGuard"]
        }]
    };

    let msgs = [msg1, msg2, msg3];
    return msgs;
}

if (msg.payload.data){
    let withdrawals = msg.payload.data.withdrawals;

    let waterconsumptionData = [];
    let maxflowrateData = [];
    // TODO: hotwater_share, hotwater_share, water_cost, energy_cost

    for (const item of withdrawals) {
        let start = new Date(item.starttime);
        let stop = new Date(item.stoptime);

        let waterconsumptionPoint1 = {
            x : start,
            y: item.waterconsumption
        };
        let waterconsumptionPoint2 = {
            x: stop,
            y: 0
        };
        waterconsumptionData.push(waterconsumptionPoint1);
        waterconsumptionData.push(waterconsumptionPoint2);

        let maxflowratePoint1 = {
            x: start,
            y: item.maxflowrate
        };
        let maxflowratePoint2 = {
            x: stop,
            y: 0
        };
        maxflowrateData.push(maxflowratePoint1);
        maxflowrateData.push(maxflowratePoint2);
    }

    let msg1 = { 
        payload : [{
            series: ["Water Consumption"],
            data: [waterconsumptionData],
            labels: ["SenseGuard"]
        }]
    };

    let msg2 = {
        payload: [{
            series: ["Max Flowrate"],
            data: [maxflowrateData],
            labels: ["SenseGuard"]
        }]
    };

    let msgs = [msg1, msg2];
    return msgs;
}
